$value = null;

$index = -1;

for($i=0; $i<=strlen($string)-1; $i++){

	if($string[$i] == '^'){

		if($value != null){

			$pairs[$index][$switch] = $value;

			$value = null;

		}

		$switch = 'biblio';

		$index++;

	}

	elseif($string[$i] == '`'){

		if($value != null){

			$pairs[$index][$switch] = $value;

			$value = null;

		}

		$switch = 'pdf';

	}



	if($string[$i] != '^' && $string[$i] != '`'){

		$value .= $string[$i];

	}

}



foreach($pairs as $key => $values){

	$biblio = $values['biblio'];

	$years[$key] = substr($biblio, -6, 4);

}



foreach($pairs as $key => $values){

	$biblio = mysql_real_escape_string($values['biblio']);

	$pdf = mysql_real_escape_string($values['pdf']);

	$year = $years[$key];

	$query = "INSERT INTO publications (Bibliography, PDF, Year, Type) VALUES ('$biblio', '$pdf', '$year', 'Proceeding')";

